  <script>
    console.log(outcomeResults);
    var output = document.getElementById('outcomes');

    // loop over all users
    outcomeResults.users.forEach(function (user) {
      console.log(user);
      var page = document.createElement('page');
      $(page).append('<h1>M-PTM3-M31</h1><div class="student"><strong>Student: </strong>'+user.display_name+'</div><div class="date"><strong>Datum: </strong>'+new Date().toJSON().slice(0,10)+'</div>');

      var table = document.createElement('table');

      outcomeResults.outcomes.reverse().forEach(function (outcome) {
        var row = document.createElement('tr');
        var cell = document.createElement('td');
        cell.innerHTML = outcome.title;
        row.appendChild(cell);

        outcome.ratings.sort(function(a,b){return b.points < a.points;}).forEach(function(rating, index){
          var cell = document.createElement('td');
          cell.innerHTML = rating.description;

          var result = outcomeResults.outcome_results.filter(function(result){
            return result.links.user == user.id && result.links.learning_outcome == outcome.id && result.score === index+1;
          });
          result.forEach(function(item){
            var span = document.createElement('span');
            span.innerHTML = item.submitted_or_assessed_at;
            cell.appendChild(span);
            cell.classList.add('reached');
          })

          row.appendChild(cell);
        });

        table.appendChild(row);
      });

      page.appendChild(table);
      output.appendChild(page);
    });

  </script>

